import Image from 'next/image';

interface OptimizedImageProps {
  src: string;
  alt: string;
  width: number;
  height: number;
  priority?: boolean;
  className?: string;
}

// 軽量なblur placeholder（1x1ピクセルのグレー画像）
const BLUR_DATA_URL =
  'data:image/jpeg;base64,';

export function OptimizedImage({
  src,
  alt,
  width,
  height,
  priority = false,
  className,
}: OptimizedImageProps) {
  return (
    <Image
      src={src}
      alt={alt}
      width={width}
      height={height}
      priority={priority}
      placeholder="blur"
      blurDataURL={BLUR_DATA_URL}
      className={className}
      style={{
        objectFit: 'cover',
      }}
    />
  );
}
